mapper.select:
select count(*) from express_receive where user_id=#{user_id}
and state=#{state}
and express_taker_id=#{express_taker_id}
and DATE_FORMAT(add_time,'%Y-%m-%d')=DATE_FORMAT(#{add_time},'%Y-%m-%d')
and deleted=false

select count(*) from express_receive where user_id=#{user_id}
and (state=2 or state=4 or state=7)
and deleted=false
